import React from 'react';
import data from "../data"
import { useParams } from 'react-router-dom';
import Rating from '../component/Rating';

export default function ProductScreen(props) {

    const { id } = useParams();

    const product = data.products.filter((pro) => pro._id === id)
    console.log("product", product)

    return (
        <div>
            <div className="row">
                <div className="col-2">
                    <img src={"../" + product[0].image} className="large" alt="" />
                </div>
                <div className="col-1">
                    <ul>
                        <li>{product[0].name}</li>
                        <li>
                            <Rating rating={product[0].rating} numReviews={product.numReviews} />
                        </li>
                    </ul>
                </div>
                <div className="col-1">

                </div>
            </div>
        </div>
    );
}
