import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { shippingCart } from '../actions/cartActions';
import CheckoutSteps from '../component/CheckoutSteps';

export default function ShippingAddressScreen() {

    const userSignin=useSelector((state)=>state.userSignin)
    const {userInfo} =userSignin;
    if(!userInfo){
        navigate("/signin")
    }

    const cart=useSelector((state)=>state.cart)

    const dispatch = useDispatch()
    const navigate=useNavigate()
    const [Name, setName] = useState("");
    const [Address, setAddress] = useState("");
    const [City, setCity] = useState("");
    const [PostalCode, setPostalCode] = useState("");
    const [Country, setCountry] = useState("");

    const onShippingSubmit = (e) => {
        e.preventDefault()
        dispatch(shippingCart({ Name, Address, City, PostalCode, Country }))
        navigate("/payment")
    }

    return (
        <div>
            <CheckoutSteps step1 step2></CheckoutSteps>
            <form className="form" onSubmit={onShippingSubmit}>
                <div>
                    <h1> Shipping Address</h1>
                </div>
                <div>
                    <label for="name">Full Name</label>
                    <input type="text" placeholder='Enter Name' onChange={(e) => setName(e.target.value)} />
                </div>
                <div>
                    <label for="address">Address</label>
                    <input type="text" placeholder='Enter Address' onChange={(e) => setAddress(e.target.value)} />
                </div>
                <div>
                    <label for="city">City</label>
                    <input type="text" placeholder='Enter City' onChange={(e) => setCity(e.target.value)} />
                </div>
                <div>
                    <label for="code">Postal Code</label>
                    <input type="text" placeholder='Enter Postal Code' onChange={(e) => setPostalCode(e.target.value)} />
                </div>
                <div>
                    <label for="country">Country</label>
                    <input type="text" placeholder='Enter Country' onChange={(e) => setCountry(e.target.value)} />
                </div>
                <div>
                    <button className='primary' type='submit'>Countinue</button>
                </div>
            </form>
        </div>
    );
}
