import React from 'react';
import { useSelector } from 'react-redux';
import { Link, useNavigate } from 'react-router-dom';
import CheckoutSteps from '../component/CheckoutSteps';

export default function PlaceOrderScreen() {

    const navigate = useNavigate()

    const cart = useSelector((state) => state.cart)
    if (!cart.paymentMethod) {
        navigate("/payment")
    }

    const toPrice = (num) => Number(num.toFixed(2));
    cart.itemsPrice = toPrice(
        cart.cartItems.reduce((a, c) => a + c.qty * c.price, 0)
    )

    cart.shippingAddress = cart.itemsPrice > 100 ? toPrice(0) : toPrice(10)

    cart.taxPrice = toPrice(0.15 * cart.itemsPrice)

    cart.totalPrice = cart.itemsPrice + cart.taxPrice + cart.shippingAddress

    return (
        <div>
            <CheckoutSteps step1 step2 step3 step4></CheckoutSteps>
            <div className="row top">
                <div className="col-2">
                    <ul>
                        <li>
                            <div className="card card-body">
                                <h2>Shipping</h2>
                                <p>
                                    <strong>Name:</strong>{cart.shippingAddress.Name}
                                    <strong>Name:</strong>{cart.shippingAddress.Address}
                                    {cart.shippingAddress.City}
                                    {cart.shippingAddress.Postalcode}
                                    {cart.shippingAddress.Country}

                                </p>
                            </div>
                        </li>

                        <li>
                            <div className="card card-body">
                                <h2>Payment</h2>
                                <p>
                                    <strong>Method:</strong>{cart.paymentMethod}
                                </p>
                            </div>
                        </li>


                        <li>
                            <div className="card card-body">
                                <h2>Order Items</h2>
                                <ul>
                                    {
                                        cart.cartItems.map((item) => (
                                            <li key={item.product}>
                                                <div className="row">
                                                    <div>
                                                        <img src={item.image} alt={item.name} className='small' />
                                                    </div>
                                                    <div className="min-30">
                                                        <Link to={`/product/${item.product}`}>{item.name}</Link>
                                                    </div>

                                                    <div>{item.qty} X ${item.price}=${item.qty * item.price}</div>

                                                </div>


                                            </li>
                                        ))
                                    }
                                </ul>
                            </div>
                        </li>
                    </ul>
                </div>
                <div className="col-1">
<div className="card">

</div>
                </div>
            </div>
        </div>
    );
}
