import Axios from "axios"
import { USER_SIGNIN_FAIL, USER_SIGNIN_REQUEST, USER_SIGNIN_SUCCESS } from "../constants/useConstant"

export const userAction = (email, password) => async(dispatch, getState) => {

    dispatch({ type: USER_SIGNIN_REQUEST, payload: { email, password } })

    try {

        const user = await Axios.post('/users/signin', { email, password })
        dispatch({ type: USER_SIGNIN_SUCCESS, payload: user })
        localStorage.setItem("userInfo", JSON.stringify(user))

    } catch (error) {

        dispatch({ type: USER_SIGNIN_FAIL, payload: error.message })

    }
}
