import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useParams, useLocation, Link } from 'react-router-dom';
import { addToCart } from '../actions/cartActions';
import MessageBox from '../component/MessageBox';

export default function CartScreen(props) {

    const dispatch = useDispatch()
    const { id } = useParams();
    const location = useLocation()
    const qty = location.search ? Number(location.search.split('=')[1]) : 1
    let navigate = useNavigate();

    const cart = useSelector((state) => state.cart)
    const { cartItems } = cart

    useEffect(() => {
        if (id) {
            dispatch(addToCart(id, qty))
        }
    }, [dispatch, id, qty])

    const removeFromCartHandler = (id) => {
        //delete action
    }

    const checkoutHandler=()=>{
        navigate()
    }

    return (
        <div className='row top'>
            <div className="col-2">
                <h1>Shopping Cart</h1>
                {
                    cartItems.length === 0 ? <>
                        <MessageBox message="Cart is empty." />
                        <Link to="/">Go Shopping</Link></> : (
                        <ul>
                            {
                                cartItems.map((item) => (
                                    <li key={item.product}>
                                        <div className="row">
                                            <div>
                                                <img src={item.image} alt={item.name} className='small' />
                                            </div>
                                            <div className="min-30">
                                                <Link to={`/product/${item.product}`}>{item.name}</Link>
                                            </div>
                                            <div>
                                                <select value={item.qty} onChange={(e) => dispatch(addToCart(item.product, Number(e.target.value)))}>
                                                    {[...Array(item.countInStock).keys()].map(
                                                        (x) => (
                                                            <option key={x + 1} value={x + 1}>
                                                                {x + 1}
                                                            </option>
                                                        )
                                                    )}
                                                </select>
                                            </div>
                                            <div>${item.price}</div>
                                            <div>
                                                <button type="button" onClick={() => removeFromCartHandler(item.product)}>Delete</button>
                                            </div>
                                        </div>

                                        <div className="col-1">
                                            <div className="card card-body">
                                                <div>
                                                    <ul>
                                                        <li><h2>Subtotal ({cartItems.reduce((a, c) => a + c.qty, 0)} items): $
                                                            {cartItems.reduce((a, c) => a + c.price * c.qty, 0)}</h2></li>
                                                        <li>
                                                            <button type='button' className='primary block' onClick={checkoutHandler} disabled={cartItems.length === 0}>
                                                                Proceed to Checkout
                                                            </button>
                                                        </li>
                                                    </ul>
                                                </div>

                                            </div>
                                        </div>
                                    </li>
                                ))
                            }
                        </ul>
                    )
                }
            </div>
        </div>
    );
}
