import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Rating from '../component/Rating';
import { Link } from "react-router-dom"
import { productDetails } from '../actions/productAction';
import LoadingBox from '../component/LoadingBox';
import MessageBox from '../component/MessageBox';
import { useDispatch, useSelector } from "react-redux"

export default function ProductScreen(props) {

    const { id } = useParams();
    const dispatch = useDispatch()

    const productDetails = useSelector(state => state.productDetails)

    const { loading, error, product } = productDetails

    // useEffect(() => {
    //     dispatch(productDetails(id))
    // }, []);

    return (
        <div>
            {
                loading ? <LoadingBox /> :
                    error ? <MessageBox variant="danger" message={error} /> :
                        <div>
                            <Link to="/">Back to result</Link>
                            <div className="row top">
                                <div className="col-2">
                                    <img src={"../" + product.image} className="large" alt="" />
                                </div>
                                <div className="col-1">
                                    <ul>
                                        <li>{product.name}</li>
                                        <li>
                                            <Rating rating={product.rating} numReviews={product.numReviews} />
                                        </li>
                                        <li>
                                            Price: ${product.price}
                                        </li>
                                        <li>
                                            Description: {product.description}
                                        </li>
                                    </ul>
                                </div>
                                <div className="col-1">
                                    <div className="card card-body">
                                        <ul>
                                            <li>
                                                <div className="row">
                                                    <div>Price</div>
                                                    <div className="price">${product.price}</div>
                                                </div>
                                            </li>
                                            <li>
                                                <div className="row">
                                                    <div>Status</div>
                                                    <div>{product.countInStock > 0 ? <span className="success">In Stock</span> :
                                                        <span className="danger">Out of Stock</span>}</div>
                                                </div>
                                            </li>
                                            <li> <button className=" primary block">Add to Cart</button></li>
                                        </ul>
                                    </div>
                                </div>
                            </div>
                        </div>
            }
        </div>
    );
}
